    "src/main/java/com/autoclaimdefender/AutoClaimDefender.java": "package com.autoclaimdefender;

import com.autoclaimdefender.commands.*;
import com.autoclaimdefender.data.ClaimManager;
import com.autoclaimdefender.data.EconomyHandler;
import com.autoclaimdefender.listeners.*;
import org.bukkit.ChatColor;
import org.bukkit.plugin.java.JavaPlugin;

public class AutoClaimDefender extends JavaPlugin {

    private ClaimManager claimManager;
    private EconomyHandler economyHandler;
    private static AutoClaimDefender instance;

    @Override
    public void onEnable() {
        instance = this;

        // Save default config
        saveDefaultConfig();

        // Initialize managers and handlers
        claimManager = new ClaimManager(this);
        economyHandler = new EconomyHandler(this);

        // Register commands
        getCommand("claim").setExecutor(new ClaimCommand(this));
        getCommand("abandonclaim").setExecutor(new AbandonClaimCommand(this));
        getCommand("claims").setExecutor(new ListClaimsCommand(this));
        getCommand("trust").setExecutor(new TrustCommand(this));
        getCommand("untrust").setExecutor(new UntrustCommand(this));
        getCommand("trustlist").setExecutor(new TrustListCommand(this));
        getCommand("acd").setExecutor(new AdminCommand(this));

        // Register listeners
        getServer().getPluginManager().registerEvents(new AutoClaimListener(this), this);
        getServer().getPluginManager().registerEvents(new ClaimProtectionListener(this), this);
        getServer().getPluginManager().registerEvents(new ClaimVisualizerListener(this), this);
        getServer().getPluginManager().registerEvents(new PlayerJoinQuitListener(this), this);

        // Load existing claims from storage
        claimManager.loadClaims();

        getLogger().info(ChatColor.GREEN + getName() + " version " + getDescription().getVersion() + " has been enabled!");
    }

    @Override
    public void onDisable() {
        // Save all claims to storage
        if (claimManager != null) {
            claimManager.saveClaims();
        }

        getLogger().info(ChatColor.RED + getName() + " has been disabled!");
        instance = null;
    }

    public ClaimManager getClaimManager() {
        return claimManager;
    }

    public EconomyHandler getEconomyHandler() {
        return economyHandler;
    }

    public static AutoClaimDefender getInstance() {
        return instance;
    }

    public void reloadConfig() {
        super.reloadConfig();
    }
}
