    "src/main/java/com/autoclaimdefender/commands/AdminCommand.java": "package com.autoclaimdefender.commands;

import com.autoclaimdefender.AutoClaimDefender;
import com.autoclaimdefender.data.Claim;
import com.autoclaimdefender.listeners.ClaimVisualizerListener;
import com.autoclaimdefender.utils.MessageUtils;
import org.bukkit.Bukkit;
import org.bukkit.OfflinePlayer;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

import java.util.UUID;

public class AdminCommand implements CommandExecutor {

    private final AutoClaimDefender plugin;

    public AdminCommand(AutoClaimDefender plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!sender.hasPermission("autoclaimdefender.admin")) {
            MessageUtils.sendMessage(sender, "&cYou don't have permission to use admin commands.");
            return true;
        }

        if (args.length == 0) {
            showHelp(sender);
            return true;
        }

        String subCommand = args[0].toLowerCase();

        switch (subCommand) {
            case "reload" -> {
                plugin.reloadConfig();
                MessageUtils.sendMessage(sender, "&aConfiguration reloaded.");
            }
            case "info" -> {
                if (!(sender instanceof Player player)) {
                    MessageUtils.sendMessage(sender, "&cThis command can only be used by players.");
                    return true;
                }
                showClaimInfo(player);
            }
            case "delete" -> {
                if (!(sender instanceof Player player)) {
                    MessageUtils.sendMessage(sender, "&cThis command can only be used by players.");
                    return true;
                }
                deleteClaimAtLocation(player);
            }
            case "transfer" -> {
                if (args.length < 2) {
                    MessageUtils.sendMessage(sender, "&cUsage: /acd transfer <player>");
                    return true;
                }
                if (!(sender instanceof Player player)) {
                    MessageUtils.sendMessage(sender, "&cThis command can only be used by players.");
                    return true;
                }
                transferClaim(player, args[1]);
            }
            case "adminclaim" -> {
                if (!(sender instanceof Player player)) {
                    MessageUtils.sendMessage(sender, "&cThis command can only be used by players.");
                    return true;
                }
                createAdminClaim(player);
            }
            case "visualize" -> {
                if (!(sender instanceof Player player)) {
                    MessageUtils.sendMessage(sender, "&cThis command can only be used by players.");
                    return true;
                }
                toggleClaimVisualization(player);
            }
            case "save" -> {
                plugin.getClaimManager().saveClaims();
                MessageUtils.sendMessage(sender, "&aClaims data has been saved.");
            }
            default -> showHelp(sender);
        }

        return true;
    }

    private void showHelp(CommandSender sender) {
        MessageUtils.sendMessage(sender, "&a===== AutoClaimDefender Admin Commands =====&r");
        MessageUtils.sendMessage(sender, "&e/acd reload &7- Reload the configuration");
        MessageUtils.sendMessage(sender, "&e/acd info &7- Show information about the claim you're standing in");
        MessageUtils.sendMessage(sender, "&e/acd delete &7- Delete the claim at your location");
        MessageUtils.sendMessage(sender, "&e/acd transfer <player> &7- Transfer claim ownership");
        MessageUtils.sendMessage(sender, "&e/acd adminclaim &7- Create an admin claim at your location");
        MessageUtils.sendMessage(sender, "&e/acd visualize &7- Toggle claim visualization");
        MessageUtils.sendMessage(sender, "&e/acd save &7- Force save all claim data");
    }

    private void showClaimInfo(Player player) {
        Claim claim = plugin.getClaimManager().getClaim(player.getLocation());

        if (claim == null) {
            MessageUtils.sendMessage(player, "&cThere is no claim at your location.");
            return;
        }

        OfflinePlayer owner = claim.isAdminClaim() ?
                null : Bukkit.getOfflinePlayer(claim.getOwner());

        MessageUtils.sendMessage(player, "&a===== Claim Information =====&r");
        MessageUtils.sendMessage(player, "&aID: &e" + claim.getClaimId());
        MessageUtils.sendMessage(player, "&aType: &e" + (claim.isAdminClaim() ? "Admin Claim" : "Player Claim"));

        if (owner != null) {
            MessageUtils.sendMessage(player, "&aOwner: &e" + owner.getName() + " &7(" + owner.getUniqueId() + ")");
        }

        MessageUtils.sendMessage(player, "&aChunks: &e" + claim.getRegion().getChunkCount());
        MessageUtils.sendMessage(player, "&aTrusted Players: &e" + claim.getTrustedPlayers().size());
    }

    private void deleteClaimAtLocation(Player player) {
        Claim claim = plugin.getClaimManager().getClaim(player.getLocation());

        if (claim == null) {
            MessageUtils.sendMessage(player, "&cThere is no claim at your location.");
            return;
        }

        boolean success = plugin.getClaimManager().abandonClaim(claim);

        if (success) {
            MessageUtils.sendMessage(player, "&aThe claim has been deleted successfully.");

            // Stop visualization if active
            ClaimVisualizerListener visualizer = getVisualizerListener();
            if (visualizer != null && visualizer.isVisualizing(player)) {
                visualizer.stopVisualization(player);
            }
        } else {
            MessageUtils.sendMessage(player, "&cFailed to delete the claim. Please try again.");
        }
    }

    private void transferClaim(Player player, String targetName) {
        Claim claim = plugin.getClaimManager().getClaim(player.getLocation());

        if (claim == null) {
            MessageUtils.sendMessage(player, "&cThere is no claim at your location.");
            return;
        }

        OfflinePlayer targetPlayer = Bukkit.getOfflinePlayer(targetName);
        if (targetPlayer == null || (!targetPlayer.hasPlayedBefore() && targetPlayer.getUniqueId() == null)) {
            MessageUtils.sendMessage(player, "&cCould not find a player named &e" + targetName + "&c.");
            return;
        }

        // Transfer ownership
        claim.setOwner(targetPlayer.getUniqueId());
        plugin.getClaimManager().saveClaims();

        MessageUtils.sendMessage(player, "&aThe claim has been transferred to &e" + targetPlayer.getName() + "&a.");
    }

    private void createAdminClaim(Player player) {
        // Check if the location is already claimed
        if (plugin.getClaimManager().getClaim(player.getLocation()) != null) {
            MessageUtils.sendMessage(player, "&cThis location is already claimed.");
            return;
        }

        Claim claim = plugin.getClaimManager().createAdminClaim(player.getLocation());
        MessageUtils.sendMessage(player, "&aAn admin claim has been created at your location.");
    }

    private void toggleClaimVisualization(Player player) {
        Claim claim = plugin.getClaimManager().getClaim(player.getLocation());

        if (claim == null) {
            MessageUtils.sendMessage(player, "&cThere is no claim at your location.");
            return;
        }

        ClaimVisualizerListener visualizer = getVisualizerListener();
        if (visualizer == null) {
            MessageUtils.sendMessage(player, "&cClaim visualizer is not available.");
            return;
        }

        if (visualizer.isVisualizing(player)) {
            visualizer.stopVisualization(player);
            MessageUtils.sendMessage(player, "&aClaim visualization turned off.");
        } else {
            visualizer.startVisualization(player, claim);
            MessageUtils.sendMessage(player, "&aClaim visualization turned on. Move around to see the borders.");
        }
    }

    private ClaimVisualizerListener getVisualizerListener() {
        return plugin.getServer().getPluginManager().getPlugin("AutoClaimDefender") != null ? 
                (ClaimVisualizerListener) plugin.getServer().getPluginManager().getListeners(plugin).stream()
                .filter(listener -> listener.getClass().equals(ClaimVisualizerListener.class))
                .findFirst().orElse(null) : null;
    }
}
