    "src/main/java/com/autoclaimdefender/listeners/ListClaimsCommand.java": "package com.autoclaimdefender.commands;

import com.autoclaimdefender.AutoClaimDefender;
import com.autoclaimdefender.data.Claim;
import com.autoclaimdefender.data.ClaimRegion;
import com.autoclaimdefender.listeners.ClaimVisualizerListener;
import com.autoclaimdefender.utils.MessageUtils;
import org.bukkit.Bukkit;
import org.bukkit.Location;
import org.bukkit.OfflinePlayer;
import org.bukkit.World;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

import java.util.Set;
import java.util.UUID;

public class ListClaimsCommand implements CommandExecutor {

    private final AutoClaimDefender plugin;

    public ListClaimsCommand(AutoClaimDefender plugin) {
        this.plugin = plugin;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player player)) {
            MessageUtils.sendMessage(sender, "&cThis command can only be used by players.");
            return true;
        }

        // Check if player has permission
        if (!player.hasPermission("autoclaimdefender.claims")) {
            MessageUtils.sendMessage(player, "&cYou don't have permission to use this command.");
            return true;
        }

        UUID targetId = player.getUniqueId();
        String targetName = player.getName();

        // Check if an admin is viewing someone else's claims
        if (args.length > 0 && player.hasPermission("autoclaimdefender.admin")) {
            OfflinePlayer target = Bukkit.getOfflinePlayer(args[0]);
            if (target.hasPlayedBefore()) {
                targetId = target.getUniqueId();
                targetName = target.getName();
            } else {
                MessageUtils.sendMessage(player, "&cPlayer not found: &e" + args[0]);
                return true;
            }
        }

        // Get all claims for the player
        Set<Claim> claims = plugin.getClaimManager().getPlayerClaims(targetId);

        if (claims.isEmpty()) {
            if (targetId.equals(player.getUniqueId())) {
                MessageUtils.sendMessage(player, "&cYou don't have any claims.");
            } else {
                MessageUtils.sendMessage(player, "&c" + targetName + " does not have any claims.");
            }
            return true;
        }

        // Visualize the current claim if the player is in one of their claims
        Claim currentClaim = plugin.getClaimManager().getClaim(player.getLocation());
        if (currentClaim != null && claims.contains(currentClaim)) {
            ClaimVisualizerListener visualizer = getVisualizerListener();
            if (visualizer != null) {
                if (visualizer.isVisualizing(player)) {
                    visualizer.stopVisualization(player);
                    MessageUtils.sendMessage(player, "&aStopped visualizing claim.");
                } else {
                    visualizer.startVisualization(player, currentClaim);
                    MessageUtils.sendMessage(player, "&aStarted visualizing claim. Move around to see the borders.");
                }
            }
        }

        // Send header
        if (targetId.equals(player.getUniqueId())) {
            MessageUtils.sendMessage(player, "&a===== Your Claims =====&r");
        } else {
            MessageUtils.sendMessage(player, "&a===== Claims for &e" + targetName + "&a =====&r");
        }

        // Send claim information
        int i = 1;
        for (Claim claim : claims) {
            ClaimRegion region = claim.getRegion();
            World world = region.getWorld();
            int chunks = region.getChunkCount();

            // Get a representative location in the claim
            Location location = null;
            if (world != null && !region.getChunks().isEmpty()) {
                ClaimRegion.ChunkCoordinate chunk = region.getChunks().iterator().next();
                location = new Location(world, chunk.getX() * 16 + 8, 64, chunk.getZ() * 16 + 8);
            }

            StringBuilder message = new StringBuilder();
            message.append("&e").append(i).append("&a. ");

            if (world != null) {
                message.append("World: &e").append(world.getName()).append("&a, ");
            } else {
                message.append("World: &eUnknown&a, ");
            }

            message.append("Chunks: &e").append(chunks).append("&a");

            if (location != null) {
                message.append(" at &e").append(location.getBlockX()).append("&a, &e")
                        .append(location.getBlockZ());
            }

            MessageUtils.sendMessage(player, message.toString());
            i++;
        }

        // Show total count
        MessageUtils.sendMessage(player, "&aTotal claims: &e" + claims.size());

        return true;
    }

    private ClaimVisualizerListener getVisualizerListener() {
        return plugin.getServer().getPluginManager().getPlugin("AutoClaimDefender") != null ? 
                (ClaimVisualizerListener) plugin.getServer().getPluginManager().getListeners(plugin).stream()
                .filter(listener -> listener.getClass().equals(ClaimVisualizerListener.class))
                .findFirst().orElse(null) : null;
    }
}
