    "src/main/java/com/autoclaimdefender/listeners/PlayerJoinQuitListener.java": "package com.autoclaimdefender.listeners;

import com.autoclaimdefender.AutoClaimDefender;
import com.autoclaimdefender.data.Claim;
import com.autoclaimdefender.utils.MessageUtils;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.event.player.PlayerQuitEvent;

public class PlayerJoinQuitListener implements Listener {

    private final AutoClaimDefender plugin;

    public PlayerJoinQuitListener(AutoClaimDefender plugin) {
        this.plugin = plugin;
    }

    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        Player player = event.getPlayer();

        // Send welcome message if it's player's first time
        if (!player.hasPlayedBefore()) {
            if (plugin.getConfig().getBoolean("messages.welcome-first-join", true)) {
                MessageUtils.sendMessage(player, plugin.getConfig().getString("messages.first-join-message",
                        "&aWelcome to the server! Place a chest to claim land around you."));
            }
        }

        // Show claims count message if enabled
        if (plugin.getConfig().getBoolean("messages.show-claims-on-join", true)) {
            int claimCount = plugin.getClaimManager().getPlayerClaims(player.getUniqueId()).size();
            int maxClaims = player.hasPermission("autoclaimdefender.bypass.limit") ? 
                    -1 : plugin.getConfig().getInt("max-claims-per-player", 3);

            if (claimCount > 0) {
                if (maxClaims > 0) {
                    MessageUtils.sendMessage(player, "&aYou have &e" + claimCount + "&a/&e" + maxClaims + "&a claims.");
                } else {
                    MessageUtils.sendMessage(player, "&aYou have &e" + claimCount + "&a claims.");
                }
            }
        }
    }

    @EventHandler
    public void onPlayerQuit(PlayerQuitEvent event) {
        Player player = event.getPlayer();

        // Clean up any active claim visualization
        ClaimVisualizerListener visualizer = getVisualizerListener();
        if (visualizer != null && visualizer.isVisualizing(player)) {
            visualizer.stopVisualization(player);
        }

        // Clean up auto-claim data
        plugin.getClaimManager().removePlayerAutoClaim(player);
    }

    private ClaimVisualizerListener getVisualizerListener() {
        return plugin.getServer().getPluginManager().getPlugin("AutoClaimDefender") != null ? 
                (ClaimVisualizerListener) plugin.getServer().getPluginManager().getListeners(plugin).stream()
                .filter(listener -> listener.getClass().equals(ClaimVisualizerListener.class))
                .findFirst().orElse(null) : null;
    }
}
